from transformers import AutoModelForCausalLM, AutoTokenizer
from huggingface_hub import login

login('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')

model = AutoModelForCausalLM.from_pretrained('google/gemma-3-12b-it').cuda()
tokenizer = AutoTokenizer.from_pretrained('google/gemma-3-12b-it')

# making huggingface compatible conversation format

hf_conversation = []
for dialogue in conversation:
    for given_role, content in dialogue.items():
        role = 'user' if given_role == 'attacker' else 'assistant'
        # as Llama 3.1 8B Instruct is not a reasoning model, removing the thinking traces, process according to the model being used
        content = content.split('</think>')[-1].lstrip()
        hf_conversation.append({'role': role, 'content': content})


hf_conversation = hf_conversation[:-1] #remove the last assistant response, we get this response from a new model - Llama in this case

# print(hf_conversation)

# applying the respective models chat template and tokenizing

tokenized_chat = tokenizer.apply_chat_template(hf_conversation, tokenize=True, add_generation_prompt=True, return_tensors="pt").to('cuda')

# generate answer from the model
max_new_tokens = 2048
if not max_new_tokens:
    raise NotImplementedError

outputs = model.generate(tokenized_chat, max_new_tokens=max_new_tokens) 
print(tokenizer.decode(outputs[0, tokenized_chat.shape[-1]:], skip_special_tokens=True))